using System;
using System.Collections.Generic;
using System.Linq;
using UserAwards.Models.Entity;

namespace UserAwards.Models.Helpers
{
	/// <summary>
	/// The award helper.
	/// </summary>
	public static class AwardHelper
	{
		private static List<AwardModel> _awardModelList;

		/// <summary>
		/// The collection peron
		/// </summary>
		public static List<AwardModel> AwardModelList
		{
			get { return _awardModelList ?? (_awardModelList = GetAwardModelList()); }
			set
			{
				if (!Equals(_awardModelList, value))
				{
					_awardModelList = value;
				}
			}
		}

		/// <summary>
		/// Gets Awardmodel entity.
		/// </summary>
		/// <param name="id">The id AwardModel.</param>
		/// <returns>Return Awardmodel.</returns>
		public static AwardModel GetAwardModelEntity(Guid id)
		{
			var res = AwardModelList.FirstOrDefault(_ => _.Id == id);
			return res;
		}

		/// <summary>
		/// Gets AwardModel collection.
		/// </summary>
		/// <returns>Return collection AwardModel.</returns>
		private static List<AwardModel> GetAwardModelList()
		{
			var awardModelList = GetAwardList().Select(_ =>
				new AwardModel
				{
					Id = _.Id,
					Title = _.Title,
					Description = _.Description,
					ImageData = _.ImageData,
					ImageMimeType = _.ImageMimeType
				}).ToList();

			return awardModelList;
		}


		/// <summary>
		/// The created new AwardModel entity.
		/// </summary>
		/// <param name="model">The AwardModel.</param>
		public static void CreateEntity(AwardModel model)
		{
			model.Id = Guid.NewGuid();
			AwardModelList.Add(model);
		}

		/// <summary>
		/// Gets award by id.
		/// </summary>
		public static AwardModel GetAwardById(Guid awardId)
		{
			return AwardModelList.FirstOrDefault(_ => _.Id == awardId);
		}

		/// <summary>
		/// The created new AwardModel entity.
		/// </summary>
		/// <param name="newModel">The AwardModel.</param>
		public static void UpdateEntity(AwardModel newModel)
		{
			var awardModel = GetAwardModelEntity(newModel.Id);
			if (awardModel != null)
			{
				awardModel.Title = newModel.Title;
				awardModel.Description = newModel.Description;
				awardModel.ImageData = newModel.ImageData;
				awardModel.ImageMimeType = newModel.ImageMimeType;
			}
		}

		/// <summary>
		/// Deleted AwardModel entity.
		/// </summary>
		/// <param name="id">The id AwardModel.</param>
		public static void DeleteEntity(Guid id)
		{
			AwardModelList.Remove(GetAwardModelEntity(id));
		}

